import { useSelector, useDispatch } from 'react-redux';
import { 
  addTodo, 
  curitem,
  setItem,
} from './canvastodoSlice.js';

export function CanvasTodo () {

  const item = useSelector(curitem);
  const dispatch = useDispatch();

  const addtoList = () => {
    dispatch(addTodo({
      todo: item,
    }));
  };

  const textChange = (input) => {
    dispatch(setItem({
      item: input,
    }));
  };

  return (
    <main>
      <label>Canvas todo list </label>

      <input type="text" value={item} onChange={e => textChange(e.target.value)}></input>
      <button onClick={addtoList}>  </button>



    </main>
  );


}
