import { createSlice } from "@reduxjs/toolkit";

const canvastodoSlice = createSlice({
  name: 'canvasTodo',
  initialState: {
    todolist: [],
    item: '',
    buttonid: 0,
  },
  reducers: {
    addTodo: (canvasTodo, action) => {
      const {
        todo,
      } = action.payload;
      canvasTodo.item = todo;
      canvasTodo.todolist.push(canvasTodo.item);
    },
    setItem: (canvasTodo, action) => {
      const {
        item,
      } = action.payload
      canvasTodo.item = item;
    },
  },
  flag: (canvasTodo, action) => {
    const {
      buttonid,
    } = action.payload
    canvasTodo.buttonid = buttonid;
  },

});

export function selectItem(state) {
  return state.canvasTodo.item;
}
export function selectListOfItems(state) {
  return state.canvasTodo.todolist;
}
export const {
  addTodo,
  setItem,
  flag,
} = canvastodoSlice.actions;

export default canvastodoSlice;
