import { createSlice } from "@reduxjs/toolkit";

const canvastodoSlice = createSlice({
  name: 'canvasTodo',
  initialState: {
    todolist: [],
    item: '',
  },
  reducer: {
    addTodo: (canvasTodo, action) => {
      const {
        todo,
      } = action.payload;
      canvasTodo.todolist.append(todo);
    },
    setItem: (canvasTodo, action) => {
      const {
        item,
      } = action.payload
      canvasTodo.item = item;
    }
  },

});

export function curitem(state) {
  return state.canvasTodo.item;
}
export const {
  addTodo,
  setItem,
} = canvastodoSlice.actions;

export default canvastodoSlice;
