import { useSelector, useDispatch } from 'react-redux';
import styles from './canvastodo.module.css';
import { 
  addTodo, 
  selectItem,
  setItem,
  selectListOfItems,
  flag,
  selectButtonId,
} from './canvastodoSlice.js';

export function CanvasTodo () {

  const todo = useSelector(selectItem);
  const listOfTodo = useSelector(selectListOfItems);
  const buttonid = useSelector(selectButtonId);
  const dispatch = useDispatch();

  const addtoList = () => {
    dispatch(addTodo({
      todo,
    }));
  };

  const textChange = (input) => {
    dispatch(setItem({
      item: input,
    }));
  };
  const onClick = (id) => {
    dispatch(flag({
      buttonid: id,
    }));
  };

  return (
    <main>
      <div>
        <label>Canvas todo list </label><br/>
        <input type="text" value={todo} onChange={(event) => textChange(event.target.value)}/>
        <button onClick={addtoList}>  add todo </button> <br/>
        <PriorityBar onClick={onClick(PriorityBar.button.className)}/>
      </div>
      <div>
        <ul>
          {
          listOfTodo.map((item) => <li>{item}</li>)
          }
        </ul>
      </div>
    </main>
  );
}


export function PriorityBar(props) { 
  return (
    <main>
      <button className={styles.green} onClick={props.onClick}> one </button>
      <button className={styles.yellow} onClick={props.onClick}> two </button> 
      <button className={styles.red} onClick={props.onClick}> three </button>
    </main>  
  );
}
