import { useSelector, useDispatch } from 'react-redux';
import { 
  addTodo, 
  curitem,
  setItem,
} from './canvastodoSlice.js';

export function CanvasTodo () {

  const todo = useSelector(curitem);
  const dispatch = useDispatch();

  const addtoList = () => {
    dispatch(addTodo({
      todo,
    }));
  };

  const textChange = (input) => {
    dispatch(setItem({
      item: input,
    }));
  };

  return (
    <main>
      <label>Canvas todo list </label>
      <div>
        <input type="text" value={todo} onChange={e => textChange(e.target.value)}></input>
        <button onClick={addtoList}>  </button>

      </div>

    </main>
  );


}
