import { useSelector, useDispatch } from 'react-redux';
import styles from './canvastodo.module.css';
import { 
  addTodo, 
  selectItem,
  setItem,
  selectListOfItems,
} from './canvastodoSlice.js';

export function CanvasTodo () {

  const todo = useSelector(selectItem);
  const listOfTodo = useSelector(selectListOfItems);
  const dispatch = useDispatch();

  const addtoList = () => {
    dispatch(addTodo({
      todo,
    }));
  };

  const textChange = (input) => {
    dispatch(setItem({
      item: input,
    }));
  };


  return (
    <main>
      <div>
        <label>Canvas todo list </label><br/>
        <input type="text" value={todo} onChange={(event) => textChange(event.target.value)}/>
        <button onClick={addtoList}>  add todo </button> <br/>
        {/* <PriorityBar/> */}
      </div>
      <div>
        <ul>
          {
          listOfTodo.map((item) => <li> <ToDoTask value={item} /> </li>)
          }
        </ul>
      </div>
      <div>
        <label>
          total number of tasks: {listOfTodo.length}
        </label>
      </div>
    </main>
  );
}

export function ToDoTask(props) {
  return (
    <>
      <input type="text" value={props.value} />
      <button>Done</button>
      <button>Comment Box</button>
    </>
  );
}
