import { useSelector, useDispatch } from 'react-redux';
import { 
  addTodo, 
  curitem,
  setItem,
  listOfItems,
} from './canvastodoSlice.js';

export function CanvasTodo () {

  const todo = useSelector(curitem);
  const listoftodo = useSelector(listOfItems);
  const dispatch = useDispatch();

  const addtoList = () => {
    dispatch(addTodo({
      todo,
    }));
  };

  const textChange = (input) => {
    dispatch(setItem({
      item: input,
    }));
  };

  return (
    <main>
      <div>
        <label>Canvas todo list </label><br/>
        <input type="text" value={todo} onChange={(event) => textChange(event.target.value)}/>
        <button onClick={addtoList}>  add todo </button> <br/>
        <PriorityBar/>

      </div>
      <div>

        <ul>
          {listoftodo.map(() => (
            <label text={listoftodo.value} />
          ))}
        </ul>

      </div>

    </main>
  );
}


export function PriorityBar(props) {
  return (
    <main>
      <button> one </button>
      <button> two </button> 
      <button> three </button>
    </main>  
  );
}
