import { useSelector, useDispatch } from 'react-redux';
import { 
  addTodo, 
  curitem,
  setItem,
} from './canvastodoSlice.js';

export function CanvasTodo () {

  const todo = useSelector(curitem);
  const dispatch = useDispatch();

  const addtoList = () => dispatch(addTodo({
    todo: todo,
  }))
  const textChange = () => dispatch(setItem({
    item: todo.target.value,
  }));
  return (
    <main>
      <label>Canvas todo list </label>
      <div>
        <input type="text" text={todo} onChange={textChange}/>
        <button> onClick={addtoList} </button>

      </div>

    </main>
  );


}
